svg.script:
let ballRadius = 12;
let x = 364;
let y = 310;
let dx = 4;
let dy = -5;
let animation_id;

function $(sel) {
  return document.querySelector(sel);
}

function draw() {

  $("#bl").setAttribute("x", x);
  $("#bl").setAttribute("y", y);

  if(x + dx > 728-ballRadius || x + dx < ballRadius) {
      dx = -dx;
  }
  if(y + dy > 382-ballRadius || y + dy < ballRadius) {
      dy = -dy;
  }

  x += dx;
  y += dy;

  animation_id = requestAnimationFrame(draw);

}

animation_id = requestAnimationFrame(draw);
