svg.script:
let autoplaying = false;

let ballRadius = 12;
let x = 364;
let y = 300;
let dx = 2;
let dy = -2;
let animation_id;
let paddleHeight = 40;
let paddleWidth = 160;
let paddleX = 364;
let rightPressed = false;
let leftPressed = false;
let enterPressed = false;
let allow_to_play = true;
let brickRowCount = 6;
let brickColumnCount = 11;
let brickWidth = 64;
let brickHeight = 24;
let brickPadding = 2;
let brickOffsetTop = 2;
let brickOffsetLeft = 2;
let allow_control = true;
let score = 0;

let bricks = [];

function $(sel) {
  return document.querySelector(sel);
}

function randomIntFromInterval(min, max) {
  return Math.floor(Math.random() * (max - min + 1) + min);
}

for (let c = 0; c < brickColumnCount; c++) {
  bricks[c] = [];
  for (let r = 0; r < brickRowCount; r++) {
    bricks[c][r] = { x: 0, y: 0, status: 1 };
  }
}

document.addEventListener("keydown", keyDownHandler, false);
document.addEventListener("keyup", keyUpHandler, false);
document.addEventListener("mousemove", mouseMoveHandler, false);

function keyDownHandler(e) {
  if(e.key == "Right" || e.key == "ArrowRight") {
    rightPressed = true;
  }
  else if(e.key == "Left" || e.key == "ArrowLeft") {
    leftPressed = true;
  }
  else if(event.keyCode === 13) {
    enterPressed = true;
    if(!allow_control) {
      restart();
    }
  }
}

function keyUpHandler(e) {
  if(e.key == "Right" || e.key == "ArrowRight") {
    rightPressed = false;
  }
  else if(e.key == "Left" || e.key == "ArrowLeft") {
    leftPressed = false;
  }
  else if(event.keyCode === 13) {
    enterPressed = false;
  }
}

function mouseMoveHandler(e) {
  let relativeX = e.clientX - $("#game").getBoundingClientRect().left;
  if(relativeX > 0 && relativeX < $("#game").getBoundingClientRect().width) {
  if (allow_control && !autoplaying) {
      paddleX = relativeX - paddleWidth/2;
      if (paddleX + 90 > 728){
        paddleX = 638;
      }
      if (paddleX < 90){
        paddleX = 90;
      }
    }
  }
}

function collisionDetection() {
  for (let c = 0; c < brickColumnCount; c++) {
    for (let r = 0; r < brickRowCount; r++) {
      let b = bricks[c][r];
      if (b.status == 1) {
        if (x > b.x && x < b.x + brickWidth && y > b.y && y < b.y + brickHeight) {
          dy = -dy;
          b.status = 0;
          score++;
        }
        if ((x == b.x || x == b.x + brickWidth) && y > b.y && y < b.y + brickHeight) {
          dx = -dx;
        }
      }
    }
  }
}

function drawBricks() {
  for (let c = 0; c < brickColumnCount; c++) {
    for (let r = 0; r < brickRowCount; r++) {
      if (bricks[c][r].status == 1) {
        let brickX = (c * (brickWidth + brickPadding)) + brickOffsetLeft;
        let brickY = (r * (brickHeight + brickPadding)) + brickOffsetTop;
        bricks[c][r].x = brickX;
        bricks[c][r].y = brickY;
      } else {
        $("#brk" + c + "_" + r).setAttribute("display", "none");
      }
    }
  }
}

function restart() {
  cancelAnimationFrame(animation_id);
  for (let c = 0; c < brickColumnCount; c++) {
    for (let r = 0; r < brickRowCount; r++) {
      bricks[c][r].status = 1;
      $("#brk" + c + "_" + r).setAttribute("display", "inline");
    }
  }
  score = 0;
  allow_control = true;
  allow_to_play = true;
  y = 170;
  x = randomIntFromInterval(100, 600);
  dx = dx * (Math.random() < 0.5 ? -1 : 1);
  $("#game_over").setAttribute("display", "none");
  $("#bl").setAttribute("display", "inline");
  animation_id = requestAnimationFrame(draw);
}

$("#game_bttn").onclick = function() {
  restart();
}

function draw() {
  drawBricks();
  $("#bl").setAttribute("x", x);
  $("#bl").setAttribute("y", y);
  $("#pddl").setAttribute("x", paddleX);
  collisionDetection();

  if(x + dx > 728-ballRadius || x + dx < ballRadius) {
      dx = -dx;
  }
  if(y + dy < ballRadius) {
      dy = -dy;
  } else if (y + dy >= 342-ballRadius) {
    if(x > paddleX - 90 && x < paddleX + 90 && y < 342) {
      dy = -dy;
    } else {
      allow_to_play = false;
    }
  }

  if (allow_control && !autoplaying) {
    if(rightPressed) {
      paddleX += 7;
      if (paddleX + 90 > 728){
        paddleX = 638;
      }
    }
    else if(leftPressed) {
      paddleX -= 7;
      if (paddleX < 90){
        paddleX = 90;
      }
    }
  }

  x += dx;
  y += dy;

  if (autoplaying) {
    if (x >= 90 && x <= 638) {
      paddleX = x;
    } else if (x < 90) {
      paddleX = 90;
    } else {
      paddleX = 638;
    }
  }

  if (allow_to_play && score < 66) {
    animation_id = requestAnimationFrame(draw);
  } else if (score < 66) {
    allow_control = false;
    animation_id = requestAnimationFrame(draw);
    $("#game_over").setAttribute("display", "inline");
    if (y > 1000) {
      $("#bl").setAttribute("display", "none");
      cancelAnimationFrame(animation_id);
    }
  } else {
    restart();
  }

}

x = randomIntFromInterval(100, 600);
dx = dx * (Math.random() < 0.5 ? -1 : 1);
animation_id = requestAnimationFrame(draw);
