svg.script:
let start;
let total_time = 10000;
let timeframe = [2000, 3000];
let lock = [true, true];

function $(sel) {
  return document.querySelector(sel);
}

var anim_easing = function (t) { return t<.5 ? 2*t*t : -1+(4-2*t)*t };

function animate(timestamp) {
  if (!start) {
    start = timestamp;
  }
  const progress = timestamp - start;
  if (progress < total_time) {
    requestAnimationFrame(animate);
  } else {
    start = timestamp;
  }

  if(progress < timeframe[0]) {
    let local_progress = progress / timeframe[0];
    if(local_progress > .999) {
      local_progress = 1;
    }
    $("#soil_clip rect").setAttribute("height", 405*anim_easing(local_progress));
    $("#root").setAttribute("transform", "translate(989 1178) scale(" + anim_easing(local_progress) + ") translate(-989 -1178)");
    $("#plant").setAttribute("transform", "translate(989 1178) scale(" + anim_easing(local_progress) + ") translate(-989 -1178)");
    $("#hemishpere").setAttribute("stroke-dashoffset", 2432*anim_easing(1 - local_progress));
    $("#sun").setAttribute("transform", "rotate(-" + 80 * anim_easing(1 - local_progress) + " 991 1076)");
  }
  if(progress > timeframe[0] && progress < timeframe[1]) {
    let local_progress = (progress - timeframe[0]) / (timeframe[1] - timeframe[0]);
    if(local_progress > .999) {
      local_progress = 1;
    }
    if(lock[1]) {
      $("#sun").setAttribute("transform", "");
      $("#sun").setAttribute("class", "animated");
      $("#ray").setAttribute("class", "animated");
      $("#carbon1").setAttribute("class", "animated");
      $("#carbon2").setAttribute("class", "animated");
      $("#arrows").setAttribute("class", "animated");
      lock[1] = false;
    }
    $("#carbon1").setAttribute("opacity", local_progress);
    $("#carbon2").setAttribute("opacity", local_progress);
    $("#ray").setAttribute("opacity", local_progress);
    $("#arr_path").setAttribute("opacity", local_progress);
    $("#arrows").setAttribute("opacity", local_progress);
  }
}

requestAnimationFrame(animate);
