svg.script:
let start;
let non_paused = true;
let total_time = 50000;
let timeframe = [2000, 8000, 16000, 17000, 20000, 24000, 35000, 40000];
let lock = [true, true, true, true, true, true, true, true];

let hum = .5;
let trm = .5;
let hum_trm = .5;

let pause = false;

function $(sel) {
  return document.querySelector(sel);
}

let anim_easing = function (t) { return t<.5 ? 2*t*t : -1+(4-2*t)*t };

$("#pause_play").onclick = function() {
  if(!pause) {
    pause = true;
    $("#circle_arrows").setAttribute("class", "animated paused");
    $("#sun").setAttribute("class", "animated paused");
    $("#ray").setAttribute("class", "animated paused");
    $("#carbon1").setAttribute("class", "animated paused");
    $("#carbon2").setAttribute("class", "animated paused");
    $("#carbon3").setAttribute("class", "animated paused");
    $("#carbon4").setAttribute("class", "animated paused");
    $("#micros2").setAttribute("class", "animated paused");
    $("#pause").setAttribute("opacity", 0);
    $("#hpause").setAttribute("opacity", 0);
    $("#play").setAttribute("opacity", 1);
    $("#hplay").setAttribute("opacity", 1);
  } else {
    pause = false;
    $("#circle_arrows").setAttribute("class", "animated");
    $("#sun").setAttribute("class", "animated");
    $("#ray").setAttribute("class", "animated");
    $("#carbon1").setAttribute("class", "animated");
    $("#carbon2").setAttribute("class", "animated");
    $("#carbon3").setAttribute("class", "animated");
    $("#carbon4").setAttribute("class", "animated");
    $("#micros2").setAttribute("class", "animated");
    $("#pause").setAttribute("opacity", 1);
    $("#hpause").setAttribute("opacity", 1);
    $("#play").setAttribute("opacity", 0);
    $("#hplay").setAttribute("opacity", 0);
  }
}

function set_speed() {
  hum_trm = (hum + trm) / 2;
  let time_rotate = 2 + (10 * (1 - hum_trm));
  $("#hum_clip_rect").setAttribute("transform", "translate(0 " + 82 * (1 - hum) + ")");
  $("#therm_clip_rect").setAttribute("transform", "translate(0 " + 92 * (1 - trm) + ")");
  $("#circle_arrows").setAttribute("style", "--circle_speed:" + time_rotate + "s");
}

$("#hum_minus").onclick = function() {
  if(hum >= .11) {
    hum -= .2;
  } else {
    hum = 0;
  }
  set_speed();
};

$("#hum_plus").onclick = function() {
  if(hum <= .89) {
    hum += .2;
  } else {
    hum = 1;
  }
  set_speed();
};

$("#therm_minus").onclick = function() {
  if(trm >= .11) {
    trm -= .2;
  } else {
    trm = 0;
  }
  set_speed();
};

$("#therm_plus").onclick = function() {
  if(trm <= .89) {
    trm += .2;
  } else {
    trm = 1;
  }
  set_speed();
};

function animate(timestamp) {
  if (!start) {
    start = timestamp;
  }
  const progress = timestamp - start;
  if (progress < total_time) {
    if (non_paused) {
      requestAnimationFrame(animate);
    }
  } else {
    start = timestamp;
  }

  if(progress < timeframe[0]) {
    let local_progress = progress / timeframe[0];
    if(local_progress > .99) {
      local_progress = 1;
    }
    $("#soil_clip rect").setAttribute("height", 405*anim_easing(local_progress));
    $("#root").setAttribute("transform", "translate(989 1178) scale(" + anim_easing(local_progress) + ") translate(-989 -1178)");
    $("#plant").setAttribute("transform", "translate(989 1178) scale(" + anim_easing(local_progress) + ") translate(-989 -1178)");
    $("#hemishpere").setAttribute("stroke-dashoffset", 2432*anim_easing(1 - local_progress));
    $("#sun").setAttribute("transform", "rotate(-" + 80 * anim_easing(1 - local_progress) + " 991 1076)");
  }
  if(progress > timeframe[0] && progress < timeframe[1]) {
    let local_progress = (progress - timeframe[0]) / 1000;
    if(local_progress > .98) {
      local_progress = 1;
    }

    if(lock[1]) {
      $("#sun").setAttribute("transform", "");
      $("#sun").setAttribute("class", "animated");
      $("#ray").setAttribute("class", "animated");
      $("#carbon1").setAttribute("class", "animated");
      $("#carbon2").setAttribute("class", "animated");
      $("#arrows").setAttribute("class", "animated");
      lock[1] = false;
    }
    $("#carbon1").setAttribute("opacity", local_progress);
    $("#carbon2").setAttribute("opacity", local_progress);
    $("#ray").setAttribute("opacity", local_progress);
    $("#arr_path").setAttribute("opacity", local_progress);
    $("#arrows").setAttribute("opacity", local_progress);
  }
  if(progress > timeframe[1] && progress < timeframe[2]) {
    let local_progress = (progress - timeframe[1]) / 1000;
    if(local_progress > .98) {
      local_progress = 1;
    }
    $("#zoom1_bg").setAttribute("opacity", local_progress);
    $("#zoom1_bg_arrow").setAttribute("transform", "translate(" + (292 * (1 - local_progress)) + " -" + (71.5 * (1 - local_progress)) + ")");
    $("#zoom1_bg_line").setAttribute("stroke-dashoffset", 356.5 * (1 - local_progress));

    let local_progress2 = (progress - timeframe[1] - 1000) / 1000;
    if(local_progress2 > .98) {
      local_progress2 = 1;
    }

    if(local_progress > .98) {
      $("#zoom1").setAttribute("opacity", local_progress2);
    }

    if(local_progress > .98 && lock[2]) {
      $("#z1_arrows").setAttribute("class", "animated");
      $("#z1_sun").setAttribute("class", "animated");
      lock[2] = false;
    }
  }
  if(progress > timeframe[2] && progress < timeframe[3]) {
    let local_progress = (progress - timeframe[2]) / 1000;
    if(local_progress > .98) {
      local_progress = 1;
    }

    $("#zoom1_bg").setAttribute("opacity", (1 - local_progress));
    $("#zoom1").setAttribute("opacity", (1 - local_progress));

    if(local_progress > .98) {
      $("#z1_arrows").setAttribute("class", "");
      $("#z1_sun").setAttribute("class", "");
    }
  }
  if(progress > timeframe[3] && progress < timeframe[4]) {
    let local_progress = (progress - timeframe[3]) / 1000;
    if(local_progress > .98) {
      local_progress = 1;
    }
    $("#to_micro").setAttribute("transform", "translate(0 -" + (210 * (1 - anim_easing(local_progress))) + ")");
    $("#to_micro").setAttribute("opacity", local_progress);
    $("#to_micro_rect").setAttribute("transform", "translate(896 910) scale(1 " + anim_easing(local_progress) + ") translate(-896 -910)");

    let local_progress2 = (progress - timeframe[3] - 1000) / 1000;
    if(local_progress2 > .98) {
      local_progress2 = 1;
    }

    if(local_progress > .98) {
      $("#micros").setAttribute("opacity", local_progress2);
    }
    if(local_progress > .98 && lock[3]) {
      $("#micros").setAttribute("class", "animated");
      $("#arrows_down").setAttribute("class", "animated");
      lock[4] = false;
    }

    let local_progress3 = (progress - timeframe[3] - 2000) / 1000;
    if(local_progress3 > .98) {
      local_progress3 = 1;
    }

    if(local_progress2 > .98) {
      $("#from_micro").setAttribute("transform", "translate(0 " + (135 * (1 - anim_easing(local_progress3))) + ")");
      $("#from_micro").setAttribute("opacity", local_progress3);
      $("#arrows_down").setAttribute("opacity", local_progress3);
      $("#from_micro_rect").setAttribute("transform", "translate(1078 1119) scale(1 " + anim_easing(local_progress3) + ") translate(-1078 -1119)");
    }
  }
  if(progress > timeframe[4] && progress < timeframe[5]) {
    let local_progress = (progress - timeframe[4]) / 1000;
    if(local_progress > .98) {
      local_progress = 1;
    }
    $("#micros").setAttribute("opacity", (1 - local_progress));
    $("#micros2").setAttribute("opacity", local_progress);
    if(lock[5]) {
      $("#micros").setAttribute("class", "");
      $("#micros2").setAttribute("class", "animated");
      lock[5] = false;
    }
  }
  if(progress > timeframe[5] && progress < timeframe[6]) {
    let local_progress = (progress - timeframe[5]) / 1000;
    if(local_progress > .98) {
      local_progress = 1;
    }
    $("#zoom2_bg").setAttribute("opacity", local_progress);
    $("#zoom2_bg_arrow").setAttribute("transform", "translate(" + (397.5 * (1 - local_progress)) + " -" + (280 * (1 - local_progress)) + ")");
    $("#zoom2_bg_line").setAttribute("stroke-dashoffset", 523.5 * (1 - local_progress));
    if(lock[6]) {
      $("#z2_bacts").setAttribute("class", "animated");
      $("#z2_wifi").setAttribute("class", "animated");
      lock[6] = false;
    }
    let local_progress2 = (progress - timeframe[5] - 1000) / 1000;
    if(local_progress2 > .98) {
      local_progress2 = 1;
    }
    if(local_progress > .98) {
      $("#zoom2").setAttribute("opacity", local_progress2);
    }
  }
  if(progress > timeframe[6] && progress < timeframe[7]) {
    let local_progress = (progress - timeframe[6]) / 1000;
    if(local_progress > .98) {
      local_progress = 1;
    }
    $("#zoom2_bg").setAttribute("opacity", (1 - local_progress));
    $("#zoom2").setAttribute("opacity", (1 - local_progress));
    $("#arr_path").setAttribute("opacity", (1 - local_progress));
    $("#arrows").setAttribute("opacity", (1 - local_progress));
    $("#arrows_down").setAttribute("opacity", (1 - local_progress));
    $("#to_micro").setAttribute("opacity", (1 - local_progress));
    $("#from_micro").setAttribute("opacity", (1 - local_progress));
    $("#to_micro_txt").setAttribute("opacity", (1 - local_progress));
    $("#from_micro_txt").setAttribute("opacity", (1 - local_progress));
    if(local_progress > .98) {
      $("#z2_bacts").setAttribute("class", "");
      $("#z2_wifi").setAttribute("class", "");
      $("#arr_path").setAttribute("class", "");
      $("#arrows").setAttribute("class", "");
      $("#arrows_down").setAttribute("class", "");
    }

    let local_progress2 = (progress - timeframe[6] - 1000) / 1000;
    if(local_progress2 > .98) {
      local_progress2 = 1;
    }

    if(local_progress2 > 0) {
      $("#plant").setAttribute("opacity", (1 - local_progress2));
      $("#carbon3").setAttribute("opacity", (1 - local_progress2));
      $("#carbon4").setAttribute("opacity", (1 - local_progress2));
      $("#carbon3").setAttribute("class", "animated");
      $("#carbon4").setAttribute("class", "animated"); 
      $("#plant_end").setAttribute("opacity", local_progress2);
      $("#bttm_line").setAttribute("stroke-dashoffset", 2432 * local_progress2 + 2432);
      $("#pause_play_text").setAttribute("display", "inline");
      $("#pause_play").setAttribute("display", "inline");
      $("#icn_therm").setAttribute("display", "inline");
      $("#icn_humidity").setAttribute("display", "inline");
    }

    let local_progress3 = (progress - timeframe[6] - 2000) / 1000;
    if(local_progress3 > .98) {
      local_progress3 = 1;
    }
    if(local_progress3 > 0) {
      $("#icn_therm").setAttribute("opacity", local_progress3);
      $("#icn_humidity").setAttribute("opacity", local_progress3);
      $("#sensor").setAttribute("opacity", local_progress3);
      $("#circle_arrows").setAttribute("opacity", local_progress3);
      $("#pause_play").setAttribute("opacity", local_progress3);
      $("#pause_play_text").setAttribute("opacity", local_progress3);
    }
    if(lock[7]) {
      $("#circle_arrows").setAttribute("class", "animated");
      lock[7] = false;
    }
  }
}

function initial() {
  $("#initial").setAttribute("class", "hidden");
  setTimeout(function(){
    $("#initial").setAttribute("display", "none");
    requestAnimationFrame(animate);
  }, 300);
}

$("#start_all").onclick = function() {
  initial();
}
