svg.script:
let r_angle = 0;
let y_angle = 0;
let t_angle = 0;
let g_angle = 0;
let w_angle = 0;
let arrows_shift = 1;
let animation_id;
let allow_play = true;
let setint;
let spring_pos;
let osc_pos = 0;
let osc_iter = 20;
let setint2;
let bttn_txt = true;

function $(sel) {
  return document.querySelector(sel);
}

// Popups

$("#bttn_surface").onclick = function() {
  $("#surface").classList.toggle("invis");
  if(bttn_txt) {
    $("#bttn_surface text").textContent = "SUBSEA";
    bttn_txt = false;
  } else {
    $("#bttn_surface text").textContent = "SURFACE";
    bttn_txt = true;
  }
};

$("#popup_flow_meter_close").onclick = function() {
  $("#popup_flow_meter").classList.toggle("invis");
};

$("#popup_warn_close").onclick = function() {
  $("#popup_warn").classList.toggle("invis");
};

$("#popup_intdifreg_close").onclick = function() {
  $("#popup_intdifreg").classList.toggle("invis");
};

$("#popup_demonstrate_close").onclick = function() {
  $("#popup_demonstrate").classList.toggle("invis");
};

$("#popup_restrictor_close").onclick = function() {
  $("#popup_restrictor").classList.toggle("invis");
};

$("#popup_throttling_close").onclick = function() {
  $("#popup_throttling").classList.toggle("invis");
};

$("#popup_spring_close").onclick = function() {
  $("#popup_spring").classList.toggle("invis");
};

$("#popup_stem_close").onclick = function() {
  $("#popup_stem").classList.toggle("invis");
};

function info_show() {
if($("#popup_flow_meter").classList.contains("invis") || $("#popup_demonstrate").classList.contains("invis") || $("#popup_restrictor").classList.contains("invis") || $("#popup_throttling").classList.contains("invis") || $("#popup_spring").classList.contains("invis") || $("#popup_stem").classList.contains("invis")) {
  $("#popup_flow_meter").classList.remove("invis");
  $("#popup_demonstrate").classList.remove("invis");
  $("#popup_restrictor").classList.remove("invis");
  $("#popup_throttling").classList.remove("invis");
  $("#popup_spring").classList.remove("invis");
  $("#popup_stem").classList.remove("invis");
} else {
  $("#popup_flow_meter").classList.toggle("invis");
  $("#popup_demonstrate").classList.toggle("invis");
  $("#popup_restrictor").classList.toggle("invis");
  $("#popup_throttling").classList.toggle("invis");
  $("#popup_spring").classList.toggle("invis");
  $("#popup_stem").classList.toggle("invis");
  }
}

$("#info").onclick = function() {
  info_show();
}

setTimeout(function(){
  info_show();
}, 400);

// Gauges

function gw_upd() {
  if(r_angle > y_angle) {
    $("#popup_warn").classList.add("invis");
  }
  g_angle = t_angle * 3;
  w_angle = t_angle * 2.35;
  spring_pos = $("#spring_3").getAttribute('y1');
  $("#gray_arrow").setAttribute("transform", "rotate(" + (g_angle - 150) + " 708.719 880.397)");
  $("#white_arrow").setAttribute("transform", "rotate(" + (w_angle - 150) + " 1748.044 763.201)");
  $("#valve_line").setAttribute("transform", "translate(0 " + (100 - t_angle) / 5 + ")");
  $("#valve_top").setAttribute("transform", "translate(0 " + (100 - t_angle) / 5 + ")");
  $("#valve_bottom").setAttribute("transform", "translate(0 " + (100 - t_angle) / -5 + ")");
  $("#spring_4").setAttribute("y1", 630 + (100 - t_angle) / 5);
  $("#spring_4").setAttribute("y2", 624 + (100 - t_angle) / 6.666);
  $("#spring_3").setAttribute("y2", 624 + (100 - t_angle) / 6.666);
  $("#spring_3").setAttribute("y1", 608 + (100 - t_angle) / 10);
  $("#spring_2").setAttribute("y1", 608 + (100 - t_angle) / 10);
  $("#spring_2").setAttribute("y2", 592 + (100 - t_angle) / 20);
  $("#spring_1").setAttribute("y2", 592 + (100 - t_angle) / 20);

  $("#red_label").textContent = "- " + r_angle + " (BAR)";
  $("#yellow_label").textContent = "- " + y_angle + " (BAR)";
  $("#gray_label").textContent = "- " + (g_angle / 30).toFixed(2) + " (BAR)";
  $("#white_label").textContent = "- " + (w_angle / 5).toFixed(2) + " (LPD)";

  //console.log(r_angle - y_angle);

  if (w_angle > 1 && r_angle > 0 && y_angle < r_angle && (r_angle - y_angle) <= 10) {
    if(allow_play) {
      animation_id = requestAnimationFrame(animate);
    }
    allow_play = false;
  } else {
    if(y_angle >= r_angle || (r_angle - y_angle) > 10) {
      $("#popup_warn").classList.remove("invis");
    }
    $("#gray_arrow").setAttribute("transform", "rotate(-150 708.719 880.397)");
    $("#white_arrow").setAttribute("transform", "rotate(-150 1748.044 763.201)");
    cancelAnimationFrame(animation_id);
    allow_play = true;
  }
}

function oscillate() {
  if(osc_iter > 0) {
    osc_iter--;
    if(osc_iter >= 10) {
      osc_pos++;
      $("#valve_body").setAttribute("transform", "translate(0 " + osc_pos + ")");
      $("#spring").setAttribute("transform", "translate(0 " + spring_pos * (1 + osc_pos/200) + ") scale(1 " + (1 - osc_pos / (spring_pos - 546)) + ") translate(0 " + (-1 * spring_pos * (1 + osc_pos/200)) + ")");
    } else {
      osc_pos--;
      $("#valve_body").setAttribute("transform", "translate(0 " + osc_pos + ")");
      $("#spring").setAttribute("transform", "translate(0 " + spring_pos * (1 + osc_pos/200) + ") scale(1 " + (1 - osc_pos / (spring_pos - 546)) + ") translate(0 " + (-1 * spring_pos * (1 + osc_pos/200)) + ")");
    }
  } else {
    clearInterval(setint2);
    osc_iter = 20;
    $("#popup_intdifreg").classList.add("invis");
  }
}

function start_oscillate() {
  clearInterval(setint2);
  $("#popup_intdifreg").classList.remove("invis");
  setint2 = setInterval(function(){oscillate()}, 80);
}

function rarr_dwn() {
  if(r_angle > 0 && r_angle > y_angle) {
    r_angle -= 5;
    $("#red_arrow").setAttribute("transform", "rotate(" + (r_angle - 150) + " 171.933 434.709)");
  } else {
    $("#popup_warn").classList.remove("invis");
  }
  gw_upd();
}

function rarr_up() {
  if(r_angle < 300) {
    r_angle += 5;
    $("#red_arrow").setAttribute("transform", "rotate(" + (r_angle - 150) + " 171.933 434.709)");
  }
  gw_upd();
}

function yarr_dwn() {
  if(y_angle > 0) {
    y_angle -= 5;
    $("#yellow_arrow").setAttribute("transform", "rotate(" + (y_angle - 150) + " 1455.102 434.729)");
  }
  gw_upd();
}

function yarr_up() {
  if(y_angle < 300 && r_angle > y_angle) {
    y_angle += 5;
    $("#yellow_arrow").setAttribute("transform", "rotate(" + (y_angle - 150) + " 1455.102 434.729)");
  } else {
    $("#popup_warn").classList.remove("invis");
  }
  gw_upd();
}

function tgl_dwn() {
  if(t_angle > 0) {
    t_angle -= 2;
    $("#toggle_arrow").setAttribute("transform", "rotate(" + ((100 - t_angle) * 1.8 - 180) + " 1143.412 408.396)");
  }
  gw_upd();
}

function tgl_up() {
  if(t_angle < 100) {
    t_angle += 2;
    $("#toggle_arrow").setAttribute("transform", "rotate(" + ((100 - t_angle) * 1.8 - 180) + " 1143.412 408.396)");
  }
  gw_upd();
}

$("#rb_left").onmousedown = function() {
  clearInterval(setint);
  setint = setInterval(function(){rarr_dwn()}, 200);
}
$("#rb_left").onclick = function() {rarr_dwn()};
$("#rb_left").onmouseup = function() {
  clearInterval(setint);
  start_oscillate();
};

$("#rb_right").onmousedown = function() {
  clearInterval(setint);
  setint = setInterval(function(){rarr_up()}, 200);
}
$("#rb_right").onclick = function() {rarr_up()};
$("#rb_right").onmouseup = function() {
  start_oscillate();
};

$("#tgl_left").onmousedown = function() {
  clearInterval(setint);
  setint = setInterval(function(){tgl_up()}, 200);
}
$("#tgl_left").onclick = function() {tgl_up()};

$("#tgl_right").onmousedown = function() {
  clearInterval(setint);
  setint = setInterval(function(){tgl_dwn()}, 200);
}
$("#tgl_right").onclick = function() {tgl_dwn()};

$("#yb_left").onmousedown = function() {
  clearInterval(setint);
  setint = setInterval(function(){yarr_dwn()}, 200);
}
$("#yb_left").onclick = function() {yarr_dwn()};
$("#yb_left").onmouseup = function() {
  start_oscillate();
};

window.onmouseup = function() {clearInterval(setint)};

$("#yb_right").onmousedown = function() {
  clearInterval(setint);
  setint = setInterval(function(){yarr_up()}, 200);
}
$("#yb_right").onclick = function() {yarr_up()};
$("#yb_right").onmouseup = function() {
  clearInterval(setint);
  start_oscillate();
};

function animate() {
  if(arrows_shift < 120) {
    arrows_shift += w_angle / 30;
    $("#queue").setAttribute("transform", "translate(" + arrows_shift + " 0)");
    $("#queue2").setAttribute("transform", "translate(" + arrows_shift + " 0)");
    $("#queue3").setAttribute("transform", "translate(" + arrows_shift + " 0)");
  } else {
    arrows_shift = 1;
    $("#queue").setAttribute("transform", "translate(" + arrows_shift + " 0)");
    $("#queue2").setAttribute("transform", "translate(" + arrows_shift + " 0)");
    $("#queue3").setAttribute("transform", "translate(" + arrows_shift + " 0)");
  }
    animation_id = requestAnimationFrame(animate);
}
