svg.script:
let t_angle = 0;
let w_angle = 0;
let arrows_shift = 1;
let animation_id;
let allow_play = true;
let cutaway = true;
let setint;
let spring_pos;

function $(sel) {
  return document.querySelector(sel);
}

// Popups

$("#popup_demo_close").onclick = function() {
  $("#popup_demo").classList.toggle("invis");
};

$("#popup_stem_close").onclick = function() {
  $("#popup_stem").classList.toggle("invis");
};

$("#popup_stem3d_close").onclick = function() {
  $("#popup_stem3d").classList.toggle("invis");
};

function info_show() {
if($("#popup_demo").classList.contains("invis") || $("#popup_stem").classList.contains("invis") || $("#popup_stem3d").classList.contains("invis")) {
  $("#popup_demo").classList.remove("invis");
  $("#popup_stem").classList.remove("invis");
  $("#popup_stem3d").classList.remove("invis");
} else {
  $("#popup_demo").classList.toggle("invis");
  $("#popup_stem").classList.toggle("invis");
  $("#popup_stem3d").classList.toggle("invis");
  }
}

$("#info").onclick = function() {
  info_show();
}

setTimeout(function(){
  info_show();
}, 400);

// Gauges

function gw_upd() {
  w_angle = t_angle * 2.35;
  spring_pos = $("#spring_3").getAttribute('y1');
  $("#stem_move").setAttribute("transform", "translate(0 " + t_angle * -.9 + ")");
  $("#white_arrow").setAttribute("transform", "rotate(" + (w_angle - 150) + " 1748.044 763.201)");
  $("#valve_line").setAttribute("transform", "translate(0 " + (100 - t_angle) / 5 + ")");
  $("#valve_top").setAttribute("transform", "translate(0 " + (100 - t_angle) / 5 + ")");
  $("#valve_bottom").setAttribute("transform", "translate(0 " + (100 - t_angle) / -5 + ")");
  $("#spring_4").setAttribute("y1", 630 + (100 - t_angle) / 5);
  $("#spring_4").setAttribute("y2", 624 + (100 - t_angle) / 6.666);
  $("#spring_3").setAttribute("y2", 624 + (100 - t_angle) / 6.666);
  $("#spring_3").setAttribute("y1", 608 + (100 - t_angle) / 10);
  $("#spring_2").setAttribute("y1", 608 + (100 - t_angle) / 10);
  $("#spring_2").setAttribute("y2", 592 + (100 - t_angle) / 20);
  $("#spring_1").setAttribute("y2", 592 + (100 - t_angle) / 20);

  if (w_angle > 1) {
    if(allow_play) {
      animation_id = requestAnimationFrame(animate);
    }
    allow_play = false;
  } else {
    $("#white_arrow").setAttribute("transform", "rotate(-150 1748.044 763.201)");
    cancelAnimationFrame(animation_id);
    allow_play = true;
  }
}

function tgl_dwn() {
  if(t_angle > 0) {
    t_angle -= 2;
    $("#toggle_arrow").setAttribute("transform", "rotate(" + ((100 - t_angle) * 1.8 - 180) + " 1143.412 408.396)");
  }
  gw_upd();
}

function tgl_up() {
  if(t_angle < 100) {
    t_angle += 2;
    $("#toggle_arrow").setAttribute("transform", "rotate(" + ((100 - t_angle) * 1.8 - 180) + " 1143.412 408.396)");
  }
  gw_upd();
}

$("#bttn_cutaway").onclick = function() {
 if(cutaway) {
  $("#bttn_cutaway text").textContent = "FULL";
  $("#full_part").classList.add("invis");
  $("#half_part").classList.remove("invis");
  cutaway = false;
 } else {
  $("#bttn_cutaway text").textContent = "CUTAWAY";
  $("#full_part").classList.remove("invis");
  $("#half_part").classList.add("invis");
  cutaway = true;
 }
};

$("#tgl_left").onmousedown = function() {
  clearInterval(setint);
  setint = setInterval(function(){tgl_up()}, 200);
}
$("#tgl_left").onclick = function() {tgl_up()};

$("#tgl_right").onmousedown = function() {
  clearInterval(setint);
  setint = setInterval(function(){tgl_dwn()}, 200);
}
$("#tgl_right").onclick = function() {tgl_dwn()};

window.onmouseup = function() {clearInterval(setint)};

function animate() {
  if(arrows_shift < 120) {
    arrows_shift += w_angle / 30;
    $("#queue1").setAttribute("transform", "translate(" + arrows_shift + " 0)");
    $("#queue2").setAttribute("transform", "translate(" + arrows_shift + " 0)");
    $("#queue3").setAttribute("transform", "translate(" + arrows_shift + " 0)");
    $("#queue4").setAttribute("transform", "translate(" + arrows_shift * -.91 + " " + arrows_shift * -.31 + ")");
    $("#queue5").setAttribute("transform", "translate(0 " + arrows_shift * .2521 + ")");
    $("#queue6").setAttribute("transform", "translate(0 " + arrows_shift * .2521 + ")");
    $("#queue7").setAttribute("transform", "translate(0 " + arrows_shift * .2521 + ")");
    $("#queue8").setAttribute("transform", "translate(" + arrows_shift * -.2386 + " " + arrows_shift * -.08 + ")");
    $("#queue9").setAttribute("transform", "translate(" + arrows_shift * .2521 + " 0)");
    $("#queue10").setAttribute("transform", "translate(" + arrows_shift * -.2521 + " 0)");
    $("#queue11").setAttribute("transform", "translate(" + arrows_shift * -.2521 + " 0)");
  } else {
    arrows_shift = 1;
    $("#queue1").setAttribute("transform", "translate(" + arrows_shift + " 0)");
    $("#queue2").setAttribute("transform", "translate(" + arrows_shift + " 0)");
    $("#queue3").setAttribute("transform", "translate(" + arrows_shift + " 0)");
    $("#queue4").setAttribute("transform", "translate(" + arrows_shift * -1 + " " + arrows_shift * -.4 + ")");
    $("#queue5").setAttribute("transform", "translate(0 " + arrows_shift * .2521 + ")");
    $("#queue6").setAttribute("transform", "translate(0 " + arrows_shift * .2521 + ")");
    $("#queue7").setAttribute("transform", "translate(0 " + arrows_shift * .2521 + ")");
    $("#queue8").setAttribute("transform", "translate(" + arrows_shift * -.2386 + " " + arrows_shift * -.08 + ")");
    $("#queue9").setAttribute("transform", "translate(" + arrows_shift * .2521 + " 0)");
    $("#queue10").setAttribute("transform", "translate(" + arrows_shift * -.2521 + " 0)");
    $("#queue11").setAttribute("transform", "translate(" + arrows_shift * -.2521 + " 0)");
  }
    animation_id = requestAnimationFrame(animate);
}
