svg.script:
let animation_id;
let animation_text;
let arrows_shift = 1;

function $(sel) {
  return document.querySelector(sel);
}

$("#popup_lvdt_close").onclick = function() {
  $("#popup_lvdt").classList.toggle("invis");
  setTimeout(function(){$("#popup_lvdt").classList.toggle("none")}, 100);
};
$("#popup_inlet_close").onclick = function() {
  $("#popup_inlet").classList.toggle("invis");
  setTimeout(function(){$("#popup_inlet").classList.toggle("none")}, 100);
};
$("#popup_outlet_close").onclick = function() {
  $("#popup_outlet").classList.toggle("invis");
  setTimeout(function(){$("#popup_outlet").classList.toggle("none")}, 100);
};
$("#popup_piston_close").onclick = function() {
  $("#popup_piston").classList.toggle("invis");
  setTimeout(function(){$("#popup_piston").classList.toggle("none")}, 100);
};
$("#popup_solenoid_close").onclick = function() {
  $("#popup_solenoid").classList.toggle("invis");
  setTimeout(function(){$("#popup_solenoid").classList.toggle("none")}, 100);
};


function info_show() {
if($("#popup_lvdt").classList.contains("none") || $("#popup_inlet").classList.contains("none") || $("#popup_outlet").classList.contains("none") || $("#popup_piston").classList.contains("none") || $("#popup_piston").classList.contains("none")) {
  $("#popup_lvdt").classList.remove("none");
  $("#popup_inlet").classList.remove("none");
  $("#popup_outlet").classList.remove("none");
  $("#popup_piston").classList.remove("none");
  $("#popup_solenoid").classList.remove("none");
  setTimeout(function(){
    $("#popup_lvdt").classList.remove("invis");
    $("#popup_inlet").classList.remove("invis");
    $("#popup_outlet").classList.remove("invis");
    $("#popup_piston").classList.remove("invis");
    $("#popup_solenoid").classList.remove("invis");
  }, 40);
} else {
  $("#popup_lvdt").classList.toggle("invis");
  $("#popup_inlet").classList.toggle("invis");
  $("#popup_outlet").classList.toggle("invis");
  $("#popup_piston").classList.toggle("invis");
  $("#popup_solenoid").classList.toggle("invis");
  setTimeout(function(){
    $("#popup_lvdt").classList.toggle("none");
    $("#popup_inlet").classList.toggle("none");
    $("#popup_outlet").classList.toggle("none");
    $("#popup_piston").classList.toggle("none");
    $("#popup_solenoid").classList.toggle("none");
  }, 100);
  }
}

$("#info").onclick = function() {
  info_show();
}

setTimeout(function(){
  info_show();
}, 400);

function animate() {
  if(arrows_shift < 40) {
    arrows_shift++;
    $("#q_07").setAttribute("transform", "translate(0 " + arrows_shift * -0.75 + ")");
    $("#q_08").setAttribute("transform", "translate(" + arrows_shift * -0.75 + " 0)");
    $("#q_09").setAttribute("transform", "translate(0 " + arrows_shift * -0.75 + ")");
    $("#q_10").setAttribute("transform", "translate(" + arrows_shift * -0.75 + " 0)");
    $("#q_11").setAttribute("transform", "translate(0 " + arrows_shift * 0.75 + ")");

    $("#q2_07").setAttribute("transform", "translate(0 " + arrows_shift * -0.75 + ")");
    $("#q2_08").setAttribute("transform", "translate(" + arrows_shift * -0.75 + " 0)");
    $("#q2_10").setAttribute("transform", "translate(" + arrows_shift * -0.75 + " 0)");
    $("#q2_11").setAttribute("transform", "translate(0 " + arrows_shift * 0.75 + ")");
    $("#q3_11").setAttribute("transform", "translate(0 " + arrows_shift * 0.75 + ")");
  } else {
    arrows_shift = 1;
  }
    animation_id = requestAnimationFrame(animate);
}

let is_anim1 = true;
let is_anim2 = true;
let anim_lenght = 0;
let string_num = 0;
let monitor_text = ['Reading Requested', 'Measuring Flow', 'Reading Captured', '2.56GPD', 'Meter Dormant'];
let blink = document.createElementNS("http://www.w3.org/2000/svg", 'tspan');
blink.textContent = "|";
blink.setAttribute("class", "blinked");

function animate_text() {
  if (anim_lenght < 660) {
    anim_lenght++;
    if (anim_lenght % 4 == 0) {
      string_num++;
    }
    if(string_num <= 17) {
      $("#monitor_row1").textContent = monitor_text[0].substr(0, string_num);
    } else if (string_num == 18) {
      $("#monitor_row1").appendChild(blink);
      $("#popup_solenoid").classList.remove("hidden");
    } else if (string_num > 18 & string_num < 30) {
      $("#bellow").setAttribute("transform", "translate(1258.6 469.1) scale(1 " + (1 + (string_num - 19) * 0.06) + ") translate(-1258.6 -469.1)");
      $("#top_piston").setAttribute("transform", "translate(0 " + (string_num - 19) * 0.8 + ")");
    } else if (string_num > 30 && string_num < 41) {
      $("#arrows_1").classList.add("invis");
      $("#arrows_2").classList.remove("invis");
      $("#popup_lvdt").classList.remove("hidden");
      $("#popup_piston").classList.remove("hidden");
      $(".to_green1").classList.add("vis");
      $(".to_green2").classList.add("vis");
      $(".to_green3").classList.add("vis");
      $(".to_green4").classList.add("vis");
      $(".to_green5").classList.add("vis");
      $("#label_1").classList.add("invis");
      $("#label_2").classList.remove("invis");
      $("#r_piston").setAttribute("transform", "translate(0 " + 1.8 * (string_num - 30) + ")")
      $("#r_spring").setAttribute("transform", "translate(1258.5 683.9) scale(1 " + (1.1 - 0.03 * (string_num - 30)) + ") translate(-1258.5 -683.9)");
    } else if (string_num > 49 && string_num < 66) {
      $("#monitor_row1").textContent = monitor_text[0];
      $("#monitor_row2").textContent = monitor_text[1].substr(0, string_num - 51);
    } else if (string_num == 66) {
      $("#monitor_row2").appendChild(blink);
    } else if (string_num > 99 && string_num < 117) {
      $("#monitor_row1").textContent = monitor_text[0];
      $("#monitor_row2").textContent = monitor_text[1];
      $("#monitor_row3").textContent = monitor_text[2].substr(0, string_num - 100);
    } else if (string_num == 117) {
      $("#monitor_row3").appendChild(blink);
    } else if (string_num > 129 && string_num < 137) {
      $("#monitor_row1").textContent = monitor_text[0];
      $("#monitor_row2").textContent = monitor_text[1];
      $("#monitor_row3").textContent = monitor_text[2];
      $("#monitor_row4").textContent = monitor_text[3].substr(0, string_num - 129);
    } else if (string_num == 137) {
      $("#monitor_row4").appendChild(blink);
      $("#label_1").classList.remove("invis");
      $("#label_2").classList.add("invis");
      $("#popup_solenoid").classList.add("hidden");
      $("#popup_lvdt").classList.add("hidden");
      $("#piston_text").textContent = "returning";
    } else if (string_num > 137 && string_num < 149) {
      $("#arrows_1").classList.remove("invis");
      $("#arrows_2").classList.add("invis");
      $("#bellow").setAttribute("transform", "translate(1258.6 469.1) scale(1 " + (1 + (148 - string_num) * 0.06) + ") translate(-1258.6 -469.1)");
      $("#top_piston").setAttribute("transform", "translate(0 " + (148 - string_num) * 0.8 + ")");
      $("#r_piston").setAttribute("transform", "translate(0 " + 1.8 * (148 - string_num) + ")");
      $("#r_spring").setAttribute("transform", "translate(1258.5 683.9) scale(1 " + (1.1 - 0.03 * (148 - string_num)) + ") translate(-1258.5 -683.9)");
      $(".to_green1").classList.remove("vis");
      $(".to_green2").classList.remove("vis");
      $(".to_green3").classList.remove("vis");
      $(".to_green4").classList.remove("vis");
      $(".to_green5").classList.remove("vis");
    } else if (string_num == 149) {
      $("#popup_piston").classList.add("hidden");
    } else if (string_num > 149 && string_num < 164) {
      $("#monitor_row1").textContent = monitor_text[0];
      $("#monitor_row2").textContent = monitor_text[1];
      $("#monitor_row3").textContent = monitor_text[2];
      $("#monitor_row4").textContent = monitor_text[3];
      $("#monitor_row5").textContent = monitor_text[4].substr(0, string_num - 149);
    } else if (string_num == 164) {
      $("#monitor_row5").appendChild(blink);
    }
    if(arrows_shift < 40) {
      arrows_shift++;
      $("#q_07").setAttribute("transform", "translate(0 " + arrows_shift * -0.75 + ")");
      $("#q_08").setAttribute("transform", "translate(" + arrows_shift * -0.75 + " 0)");
      $("#q_09").setAttribute("transform", "translate(0 " + arrows_shift * -0.75 + ")");
      $("#q_10").setAttribute("transform", "translate(" + arrows_shift * -0.75 + " 0)");
      $("#q_11").setAttribute("transform", "translate(0 " + arrows_shift * 0.75 + ")");

      $("#q2_07").setAttribute("transform", "translate(0 " + arrows_shift * -0.75 + ")");
      $("#q2_08").setAttribute("transform", "translate(" + arrows_shift * -0.75 + " 0)");
      $("#q2_10").setAttribute("transform", "translate(" + arrows_shift * -0.75 + " 0)");
      $("#q2_11").setAttribute("transform", "translate(0 " + arrows_shift * 0.75 + ")");
      $("#q3_11").setAttribute("transform", "translate(0 " + arrows_shift * 0.75 + ")");
    } else {
      arrows_shift = 1;
    }
    animation_text = requestAnimationFrame(animate_text);
  } else {
    string_num = 0;
    anim_lenght = 0;
    is_anim1 = false;
    cancelAnimationFrame(animation_text);
    animation_id = requestAnimationFrame(animate);
  }
}

$("#bttn_replay").onclick = function() {
  cancelAnimationFrame(animation_text);
  cancelAnimationFrame(animation_id);
  string_num = 0;
  anim_lenght = 0;
  is_anim1 = true;
  is_anim2 = true;
  $("#monitor_row1").textContent = "";
  $("#monitor_row2").textContent = "";
  $("#monitor_row3").textContent = "";
  $("#monitor_row4").textContent = "";
  $("#monitor_row5").textContent = "";
  $("#shape_play").classList.add("invis");
  $("#shape_pause").classList.remove("invis");
  $("#popup_solenoid").classList.add("hidden");
  $("#popup_lvdt").classList.add("hidden");
  $("#popup_piston").classList.add("hidden");
  $("#piston_text").textContent = "moving";
  $("#label_1").classList.remove("invis");
  $("#label_2").classList.add("invis");
  $("#bellow").setAttribute("transform", "");
  $("#top_piston").setAttribute("transform", "");
  $("#r_piston").setAttribute("transform", "");
  $("#r_spring").setAttribute("transform", "translate(1258.5 683.9) scale(1 1.1) translate(-1258.5 -683.9)");
  $(".to_green1").classList.remove("vis");
  $(".to_green2").classList.remove("vis");
  $(".to_green3").classList.remove("vis");
  $(".to_green4").classList.remove("vis");
  $(".to_green5").classList.remove("vis");
  $("#arrows_1").classList.remove("invis");
  $("#arrows_2").classList.add("invis");
  animation_text = requestAnimationFrame(animate_text);
}

$("#bttn_pause").onclick = function() {
  $("#shape_play").classList.toggle("invis");
  $("#shape_pause").classList.toggle("invis");
  if (is_anim1 && is_anim2) {
    cancelAnimationFrame(animation_text);
    is_anim2 = false;
  } else if (is_anim1 && !is_anim2) {
    animation_text = requestAnimationFrame(animate_text);
    is_anim2 = true;
  } else if (!is_anim1 && is_anim2) {
    cancelAnimationFrame(animation_id);
    is_anim2 = false;
  } else {
    animation_id = requestAnimationFrame(animate);
    is_anim2 = true;
  }
}

animation_text = requestAnimationFrame(animate_text);
