$("#bttn").onclick = function() {
  win = 1;
  req_af = requestAnimationFrame(wheel_rotation);
  $("#bttn").setAttribute("class", "op");
  $("#initial_headline").setAttribute("class", "op");
  setTimeout(function(){
    $("#bttn").setAttribute("class", "invis op");
    $("#initial_headline").setAttribute("class", "invis op");
  }, 300)
}

function wheel_rotation() {
  if (angle < 100) {
    if(win === 1) {
      $("#sectors").setAttribute("transform", rotate_1[angle]);
    } else if (win === 2) {
      $("#sectors").setAttribute("transform", rotate_2[angle]);
    } else if (win === 3) {
      $("#sectors").setAttribute("transform", rotate_3[angle]);
    } else if (win === 4) {
      $("#sectors").setAttribute("transform", rotate_4[angle]);
    } else if (win === 5) {
      $("#sectors").setAttribute("transform", rotate_5[angle]);
    } else if (win === 6) {
      $("#sectors").setAttribute("transform", rotate_6[angle]);
    } else if (win === 7) {
      $("#sectors").setAttribute("transform", rotate_7[angle]);
    } else if (win === 8) {
      $("#sectors").setAttribute("transform", rotate_8[angle]);
    }
    angle++;
    req_af = requestAnimationFrame(wheel_rotation);
  } else {
    cancelAnimationFrame(req_af);
    $("#win").setAttribute("href", "#sector" + win);
    $("#win").setAttribute("class", "");
    $("#stars_block").setAttribute("class", "");
    req_af = requestAnimationFrame(show_prize);
  }
}

function show_prize() {
  if (prize_time < 31) {
    $("#win").setAttribute("transform", "translate(622 -300) scale(" + (1 + 0.5 * prize_scale[prize_time]) + ") translate(-622 300)");
    $("#win").setAttribute("opacity",  prize_scale[prize_time]);
    $("#stars_block").setAttribute("transform", "translate(622 780) scale(" + 2 * prize_scale[prize_time] + ") translate(-622 -780)");
    $("#stars_block").setAttribute("opacity",  prize_scale[prize_time]);
    prize_time++;
    req_af = requestAnimationFrame(show_prize);
  } else {
    $("#bttn").setAttribute("class", "op");
    $("#prize_text").setAttribute("class", "op");
    $("#next_headline").setAttribute("class", "op");
    setTimeout(function(){
      $("#bttn").setAttribute("class", "");
      $("#prize_text").setAttribute("class", "");
      $("#next_headline").setAttribute("class", "");
    }, 300)
    cancelAnimationFrame(req_af);
  }
}